import React from 'react';
import './style.css'
import { BrowserRouter, Route, Link, Switch } from "react-router-dom";
import { connect } from 'react-redux';
import { closePopedMessage } from '../actions/popUpActions';
import { uploadData } from '../actions/postActions';
import { changePopedMessage } from '../actions/popUpActions';


class Popup extends React.Component {

    constructor(props) {
        super(props);
        this.uploadChange = this.uploadChange.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }
    uploadChange (event) {
      event.preventDefault();
      this.props.updataMessage('http://localhost:3001/changeMessage/' + this.props.idx, this.props.popUpMessage,'PUT');
    }
    handleChange (event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.props.changePopedMessage(name,value);
    }
    renderMessage = () => {
      return (
                <div className='popup_inner' >
                  <form onSubmit={this.uploadChange} onChange={this.handleChange} className="form" ref = "inputform">
                    <label htmlFor="fname">From</label>
                    <input type = 'text' name = 'fromname' defaultValue = {this.props.message.fromname}/>

                    <label htmlFor="tname">To</label>
                    <input type = 'text' name = 'toname' defaultValue = {this.props.message.toname}/>

                    <label htmlFor="Category">Category</label>
                    <input type = 'text' name = 'category' defaultValue = {this.props.message.category}/>

                    <label htmlFor="subject">Content</label>
                    <textarea id="subject" name="textcontent" placeholder= {this.props.message.textcontent}></textarea>
                    <center>
                        <button type = 'submit' value="Submit" id = "submitEditBtn" >save change</button>
                        <BrowserRouter> {/* browserRouter is a router component Generally speaking, you should use a <BrowserRouter> if you have a server that responds to requests and a <HashRouter> if you are using a static file server.*/}
                          <div>
                              <Link
                                  to={'/'}
                              >Cancel/Close</Link>
                              <Switch>
                                  <Route path="/popup" 
                                  render={(props) => <Popup {...props} idx={this.props.index} />}
                                  />
                              </Switch> 
                          </div>
                        </BrowserRouter>
                        <button type = 'close' onClick={this.props.closePopedMessage}>Cancel/Close</button>
                    </center>
                </form>

                </div>
              );
    }

    render() {
      console.log(this.props.toPop);
      return (
        <div className='popup'>
            {this.props.toPop && this.renderMessage()}
        </div>
      );
    }
}

const mapStateToProps = (state) => {
    return { 
        message: state.fetchReducers.popUpMessage,
        popUpMessage: state.popMessageReducer.popUpMessage,
        toPop: state.popMessageReducer.popUp,
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
      closePopedMessage: () => {
        dispatch(closePopedMessage());
      },
      updataMessage: (url,data,method) => {
        dispatch(uploadData(url,data,method));
      },
      changePopedMessage: (name,value) =>{
        dispatch(changePopedMessage(name,value));
      }
    }
};
export default connect(mapStateToProps, mapDispatchToProps)(Popup);
