import { resetPopUpStatus } from "./fetchActions";
export const UNVIEW_ONE = 'UNVIEW_ONE';
export const CHANGE_POPUP = 'CHANGE_POPUP'
export const closePopedMessage = () => {
	return (dispatch) => {
        dispatch(resetPopUpStatus());
	};
};

export const changeUnsubmittedMessage = (key,value) => {
	return {
		type: CHANGE_POPUP,
		keyToChange: key,
		valueToUpdate : value
	};
};
