import React from 'react';
import { connect } from 'react-redux';
import { fetchSingleItem }  from '../actions/fetchActions';
import './style.css'
import Popup from './PopUp'
class SeeOne extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick(){
        console.log(this.props.index);
        this.props.showIndexMessage('http://localhost:3001/getMessages/' + this.props.index);
    }
	render() {
        return (
            <div>
                <button type="view" onClick = {this.handleClick} id={this.props.index + 'View'}>ViewInPopUp</button>
                {this.props.toPop && <Popup  />}
            </div>   
        );
    }
}
const mapStateToProps = (state) => {
    return { 
        toPop: state.fetchReducers.popUp,
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
      showIndexMessage: (url) => {
        dispatch(fetchSingleItem(url));
      }
    }
};
export default connect(mapStateToProps, mapDispatchToProps)(SeeOne);
