import { combineReducers } from 'redux';
const defaultState = [{
	fromname: 'LGY',
	toname: 'YGL',
	category : 'A',
	textcontent : 'Hello!',
	date : new Date()
}];
const messageReducer = (state = defaultState, action) => {
	switch(action.type){
		case 'GEN_MESSAGE' :
			return [...state,action.message];
		case 'CLEAR_ALL':
			return [];
		case 'CLEAR_ONE':
			return state.slice(0,action.toDelIndex).concat(state.slice(action.toDelIndex+1));
		default:
			return state;
	}
};

export default combineReducers({ 
	messageProcess: messageReducer,
    //anotherKey: anotherReducer //all your reducers should be combined
});
