import { combineReducers } from 'redux';

const submitBtnReducer = (state = {}, action) => {
	if (action.type === 'GEN_MESSAGE') {
		state = action.message;
		return state;
	}
	return state;
};
const clearAllBtnReducer = (state = {}, action) => {
	if (action.type === 'CLEAR_ALL') {
		console.log(action.message);
		state = action.message;
		return state;
	}
	return state;
};
const clearBtnReducer = (state = {}, action) => {
	if (action.type === 'CLEAR_ONE') {
		console.log(action.message);
		state = action.message;
		return state;
	}
	return state;
};
export default combineReducers({ 
	messageGeneration: submitBtnReducer,
	messagesClearance: clearAllBtnReducer,
	messageErase: clearBtnReducer
    //anotherKey: anotherReducer //all your reducers should be combined
});
