export const generateMessage = (messageJson) => {
	return {
        type: 'GEN_MESSAGE',
        fromname: messageJson.fromname,
        toname: messageJson.toname,
        category : messageJson.category,
        textcontent : '',
        date : new Date()
	};
};
export const clearAllMessages = (messages) => {
	return {
        type: 'CLEAR_ALL',
        messages: messages
	};
};
export const clearMessage = (message) => {
	return {
        type: 'CLEAR_ONE',
        message: message
	};
};
