import { combineReducers } from 'redux';
import messageReducer from './messageReducer.js';
import popMessageReducer from './popMessageReducer.js';
import changeInputReducer from './changeInputReducer.js';

export const defaultState = {
	count : 1,
	unsubmittedMessage : {
		fromname: '',
		toname: '',
		category : 'A',
		textcontent : '',
		date : new Date().toLocaleString()
	},
	popUp: false,
	popUpMessageIndex : 0,
	messageArray : []
};

export default combineReducers({ 
	messageReducer,
	popMessageReducer,
	changeInputProcess: changeInputReducer,

    //anotherKey: anotherReducer //all your reducers should be combined
});
