import React from 'react';
import './style.css'
import { connect } from 'react-redux';
import { closePopedMessage } from '../actions/popUpActions';


class Popup extends React.ComponentÂ {

    renderMessage = () => {
      return (
                <div className='popup_inner' >
                  <input type = 'text' defaultValue = {this.props.message.fromname}/>
                      <div onDoubleClick = {this.changeEditMode('fromEditMode')}>from : {this.props.message.fromname}</div>}

                  <input type = 'text' defaultValue = {this.props.message.toname}/>
                      <div onDoubleClick = {this.changeEditMode('toEditMode')}>to : {this.props.message.toname}</div>}

                  <input type = 'text' defaultValue = {this.props.message.category}/> :
                      <div onDoubleClick = {this.changeEditMode('catEditMode')}>category: {this.props.message.category}</div>}

                  <input type = 'text' defaultValue = {this.props.message.textcontent}/> :
                      <div onDoubleClick = {this.changeEditMode('contentEditMode')}>content: {this.props.message.textcontent}</div>} 

                  <input type = 'text' defaultValue = {this.props.message.date.toString()}/> :
                      <div onDoubleClick = {this.changeEditMode('dateEditMode')}>date: {this.props.message.date.toString()}</div>}

                  <button type = 'close' onClick={this.props.closePopedMessage}>Cancel/Close</button>
                  <button type = 'edit' onClick={this.props.uploadChange}>save change</button>
                </div>
              );
    }

    render() {
      return (
        <div className='popup'>
            {this.renderMessage()}

        </div>
      );
    }
}
const mapStateToProps = (state) => {
    return { 
        message: state.fetchReducers.popUpMessage
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
      closePopedMessage: () => {
        dispatch(closePopedMessage());
      }
    }
};
export default connect(mapStateToProps, mapDispatchToProps)(Popup);
