import { combineReducers } from 'redux';
const defaultState = {
	count : 1,
	messageArray : [{
	fromname: 'LGY',
	toname: 'YGL',
	category : 'A',
	textcontent : 'Hello!',
	date : new Date()
	}]
};
const messageReducer = (state = defaultState, action) => {
	switch(action.type){
		case 'GEN_MESSAGE' :
			console.log(action.message.fromname);
			console.log([...state,action.message]);
			return [...state.messageArray,action.message];
		case 'CLEAR_ALL':
			console.log(state);
			return [];
		case 'CLEAR_ONE':
			return state.slice(0,action.toDelIndex).concat(state.slice(action.toDelIndex+1));
		default:
			return state;
	}
};

export default combineReducers({ 
	messageProcess: messageReducer,
    //anotherKey: anotherReducer //all your reducers should be combined
});
