export const VIEW_ONE = 'VIEW_ONE';
export const UNVIEW_ONE = 'UNVIEW_ONE';


export const popUpMessage = (index) => {
	return {
		type: VIEW_ONE,
		toViewIndex : index
	};
};
export const closePopedMessage = () => {
	return {
		type: UNVIEW_ONE,
	};
};
