import { combineReducers } from 'redux';
const defaultState = {
	count : 1,
	unsubmittedMessage : {
		fromname: '',
		toname: 'YGL',
		category : 'A',
		textcontent : 'Hello!',
		date : new Date()
	},
	messageArray : [{
		fromname: 'LGY',
		toname: 'YGL',
		category : 'A',
		textcontent : 'Hello!',
		date : new Date()
	}]
};
const messageReducer = (state = defaultState, action) => {
	switch(action.type){
		case 'GEN_MESSAGE' :
			console.log([...state.messageArray,action.message]);
			console.log(state.count);
			return {
				count : state.count + 1,
				messageArray: [...state.messageArray,action.message]
			};
		case 'CLEAR_ALL':
			console.log(state);
			return {
				count : 0,
				messageArray: []
			};
		case 'CLEAR_ONE':
			return	{
				count : state.count - 1,
				messageArray: [state.messageArray.slice(0,action.toDelIndex).concat(state.messageArray.slice(action.toDelIndex+1))]
			};
		default:
			return state;
	}
};

export default combineReducers({ 
	messageProcess: messageReducer,
    //anotherKey: anotherReducer //all your reducers should be combined
});
