import React from 'react';
import './style.css'
import NewMessageRow from './NewMessageRow.js'
import { connect } from 'react-redux';

class MessageBox extends React.Component {    
	render() {
        return (
            <div className="messages">		
                <table id = "messagetable" style={{"wordWrap":"break-word","wordBreak":"break-all"}}>
                    <tbody>
                        <tr id = "messagetableHeader">
                            <th style={{width: '50px'}}>From</th>
                            <th style={{width: '50px'}}>To</th>
                            <th style={{width: '50px'}}>Category</th>
                            <th>Content</th>
                            <th style={{width: '80px'}} >Added Date</th>
                            <th style={{width: '20px'}}>Operation</th>
                        </tr>


                        {this.props.Messages.map((element,idx) => {

                                <tr id = "messagetableHeader">
                                    <td style={{width: '50px'}}>From</td>
                                    <td style={{width: '50px'}}>To</td>
                                    <td style={{width: '50px'}}>Category</td>
                                    <td>Content</td>
                                    <td style={{width: '80px'}} >Added Date</td>
                                    <td style={{width: '20px'}}>Operation</td>
                                </tr>
                            })
                        }
                    </tbody>
                </table>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    return { Messages: state.messageArray };
}
export default connect(mapStateToProps,null)(MessageBox);
