import { combineReducers } from 'redux';
import messageArrayActions from '../actions/messageArrayActions';
import popUpActions from '../actions/popUpActions';
import changeInputActions from '../actions/changeInputAction';
const defaultState = {
	count : 1,
	unsubmittedMessage : {
		fromname: '',
		toname: '',
		category : 'A',
		textcontent : '',
		date : new Date().toLocaleString()
	},
	popUp: false,
	popUpMessageIndex : 0,
	messageArray : []
};
const messageReducer = (state = defaultState, action) => {
	switch(action.type){
		case messageArrayActions.GEN_MESSAGE :
			// console.log(state);
			// console.log([...state.messageArray,state.unsubmittedMessage]);
			console.log(state.count);
			return {
				count : state.count + 1,
				unsubmittedMessage : state.unsubmittedMessage,
				popUp:  state.popUp,
				popUpMessageIndex : state.popUpMessageIndex,
				messageArray: [...state.messageArray,state.unsubmittedMessage]
			};
		case messageArrayActions.CLEAR_ALL:
			// console.log(state);
			return {
				count : 0,
				unsubmittedMessage : state.unsubmittedMessage,
				popUp:  state.popUp,
				popUpMessageIndex : state.popUpMessageIndex,
				messageArray: []
			};
		case messageArrayActions.CLEAR_ONE:
			return	{
				count : state.count - 1,
				unsubmittedMessage : state.unsubmittedMessage,
				popUp: state.popUp,
				popUpMessageIndex : state.popUpMessageIndex,
				messageArray: [...state.messageArray.slice(0,action.toDelIndex).concat(state.messageArray.slice(action.toDelIndex+1))]
			};
		default:
			return state;
	}
};
const popMessageReducer = (state = defaultState, action) => {
	switch(action.type){
		case popUpActions.VIEW_ONE:
			console.log('wwww'+action.toViewIndex);
			return	{
				count : state.count,
				unsubmittedMessage : state.unsubmittedMessage,
				popUp : true,
				popUpMessageIndex : action.toViewIndex,
				messageArray: state.messageArray
			};
		case popUpActions.UNVIEW_ONE:
			return	{
				count : state.count,
				unsubmittedMessage : state.unsubmittedMessage,
				popUp : false,
				popUpMessageIndex : 0,
				messageArray: state.messageArray
			};
		default:
			return state;
	}
};
const changeInputReducer = (state = defaultState, action) => {
	switch(action.type){
		case changeInputActions.CHANGE_INPUT :
			var newMessage = Object.assign({}, state.unsubmittedMessage, 
				{ 
					[action.keyToChange]: action.valueToUpdate,
					'date' : new Date()
				}
			);
			console.log(newMessage);
			return {
				count : state.count,
				unsubmittedMessage : newMessage,
				popUp : state.popUp,
				popUpMessageIndex : state.popUpMessageIndex,
				messageArray: state.messageArray
			};
		default:
			return state;
	}
};

export default combineReducers({ 
	messageProcess: messageReducer,
	popUpProcess: popMessageReducer,
	changeInputReducer
    //anotherKey: anotherReducer //all your reducers should be combined
});
