import React from 'react';
import './style.css'
import ClearOne from './ClearOne.js';
import { connect } from 'react-redux';

class MessageBox extends React.Component {    
	render() {
        console.log(this.props.messageArray);
        var rows = [];
        for(let i = 0 ; i < this.props.messageArray.length; i++){
            rows.push(
            <tr>
                <td style={{width: '50px'}}>{this.props.messageArray[i].fromname}</td>
                <td style={{width: '50px'}}>{this.props.messageArray[i].toname}</td>
                <td style={{width: '50px'}}>{this.props.messageArray[i].category}</td>
                <td>{this.props.messageArray[i].textcontent}</td>
                <td style={{width: '80px'}} >{this.props.messageArray[i].date}</td>
                <td style={{width: '20px'}}><ClearOne/></td>
            </tr>
            )
        }
        return (
            <div className="messages">		
                <table id = "messagetable" style={{"wordWrap":"break-word","wordBreak":"break-all"}}>
                    <tbody>
                        <tr id = "messagetableHeader">
                            <th style={{width: '50px'}}>From</th>
                            <th style={{width: '50px'}}>To</th>
                            <th style={{width: '50px'}}>Category</th>
                            <th>Content</th>
                            <th style={{width: '80px'}} >Added Date</th>
                            <th style={{width: '20px'}}>Operation</th>
                        </tr>
                        {rows}
                    </tbody>
                </table>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    return { messageArray: state.messageArray,
            };
}
export default connect(mapStateToProps,null)(MessageBox);
