export const generateMessage = (messageJson) => {
	return {
        type: 'GEN_MESSAGE',
        message : messageJson
	};
};
export const clearAllMessages = () => {
	return {
        type: 'CLEAR_ALL',
	};
};
export const clearMessage = (int) => {
	return {
        type: 'CLEAR_ONE',
	};
};
