	unsubmittedMessage : {
		fromname: '',
		toname: '',
		category : 'A',
		textcontent : '',
		date : new Date().toLocaleString()
	},
	popUp: false,
	popUpMessageIndex : 0,
    messageArray : [],
    fetchError: false,
    fetching: false,
    fetchSuccess: false,
    postError: false,
    posting: false,
