import { connect } from 'react-redux';
import { clearAllMessages}  from '../actions/index.js';
import './style.css'
class ClearOne extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick(){
        this.props.cOne();
    }
	render() {
        return (
            <button type="del" onClick = {this.handleClick} id="Clear">Clear</button>
        );
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
      cOne: () => {
        dispatch(clearAllMessages());
      }
    }
};
export default connect(null, mapDispatchToProps)(ClearOne);
