import defaultState from './defaultState';
import * as popUpActions from '../actions/popUpActions';

const popMessageReducer = (state = defaultState, action) => {
	switch(action.type){
		case popUpActions.UNVIEW_ONE:
			return Object.assign({}, state, 
				{ 
					popUp : false,
					popUpMessage : {},
				}
			);
		case popUpActions.CHANGE_INPUT :
				var newMessage = Object.assign({}, state.unsubmittedMessage, 
					{ 
						[action.keyToChange]: action.valueToUpdate,
						'date' : new Date()
					}
				);
				console.log(newMessage);
				return {
					count : state.count,
					unsubmittedMessage : newMessage,
					popUp : state.popUp,
					popUpMessageIndex : state.popUpMessageIndex,
					messageArray: state.messageArray
				};
		default:
			return state;
	}
};
export default popMessageReducer;
