import React from 'react';
import './style.css'
import NewMessageRow from './NewMessageRow.js'
import { connect } from 'net';
import { generateMessage } from '../actions';

class MessageBox extends React.Component {    
	render() {
        return (
            <div className="messages">		
                <p>{this.props.Messages}</p>					
                <table id = "messagetable" style={{"wordWrap":"break-word","wordBreak":"break-all"}}>
                    <tbody>
                        <tr id = "messagetableHeader">
                            <th style={{width: '50px'}}>From</th>
                            <th style={{width: '50px'}}>To</th>
                            <th style={{width: '50px'}}>Category</th>
                            <th>Content</th>
                            <th style={{width: '80px'}} >Added Date</th>
                            <th style={{width: '20px'}}>Operation</th>
                        </tr>
                        {/* {
                            this.props.Messages.map((message, idx) => {
                            return (
                            <NewMessageRow key={idx}>{message}</NewMessageRow>
                            )
                            })} */}
                    </tbody>
                </table>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    return { Messages: state.messageArray };
}
export default connect(mapStateToProps,{generateMessage})(MessageBox);
