import { resetPopUpStatus } from "./fetchActions";
export const UNVIEW_ONE = 'UNVIEW_ONE';
export const FIRE_POP_UP = 'FIRE_POP_UP';
export const CHANGE_INPUT = 'CHANGE_INPUT';
export const closePopedMessage = () => {
	return (dispatch) => {
        dispatch(resetPopUpStatus());
	};
};

export const firePopUP = () => {
	return {
        type: FIRE_POP_UP
    };
};

export const changeUnsubmittedMessage = (key,value) => {
	return {
		type: CHANGE_INPUT,
		keyToChange: key,
		valueToUpdate : value
	};
};
