import React from 'react';
import './style.css'
import { connect } from 'react-redux';
import { closePopedMessage } from '../actions/popUpActions';


class Popup extends React.ComponentÂ {
    constructor(props) {
        super(props);
        this.state = {
          fromname: '',
          toname: '',
          category: 'A',
          textcontent: ''
      };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleSubmit (event) {
        event.preventDefault();
        this.props.generateMessage();
    }
    handleChange (event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.props.changeUnsubmittedMessage(name,value);
    }
    render() {
      return (
        <div className='popup'>
          <div className='popup_inner'>
                <div>from : {this.props.message.fromname}</div>
                <div>to : {this.props.message.toname}</div>
                <div>category: {this.props.message.category}</div>
                <div>content: {this.props.message.textcontent}</div>
                <div>date: {this.props.message.date.toString()}</div>
                <button type = 'close' onClick={this.props.closePopedMessage}>close me</button>
                <button type = 'edit' onClick={this.props}>save change</button>
          </div>
        </div>
      );
    }
}
const mapStateToProps = (state) => {
    return { 
        message: state.fetchReducers.popUpMessage
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
      closePopedMessage: () => {
        dispatch(closePopedMessage());
      }
    }
};
export default connect(mapStateToProps, mapDispatchToProps)(Popup);
