export const generateMessage = (from,to,category,content,date) => {
	return {
                type: 'GEN_MESSAGE',
                from: from,
                to: to,
                category: category,
                content: content,
                date: date
	};
};
export const clearAllMessages = (messages) => {
	return {
                type: 'CLEAR_ALL',
                messages: messages
	};
};
export const clearMessage = (message) => {
	return {
                type: 'CLEAR_ONE',
                message: message
	};
};
