import React from 'react';
import './style.css'
import { connect } from 'react-redux';
import { closePopedMessage } from '../actions/popUpActions';


class Popup extends React.ComponentÂ {
    constructor(props) {
        super(props);
        this.state = {
          fromEditMode: false,
          toEditMode: false,
          catEditMode: false,
          contentEditMode: false,
          dateEditMode: false,
        };
    }

    changeEditMode = (field) => {
      var jsonVariable = {};
      jsonVariable[field] = 1;
      const newState = Object.assign({}, this.state, jsonVariable);
      this.setState(newState);
    }

    renderMessage = () => {
      return (
                <div className='popup_inner' >
                      <div onDoubleClick = {this.changeEditMode('fromEditMode')}>from : {this.props.message.fromname}</div>
                      <div onDoubleClick = {this.changeEditMode}>to : {this.props.message.toname}</div>
                      <div onDoubleClick = {this.changeEditMode}>category: {this.props.message.category}</div>
                      <div onDoubleClick = {this.changeEditMode}>content: {this.props.message.textcontent}</div>
                      <div onDoubleClick = {this.changeEditMode}>date: {this.props.message.date.toString()}</div>
                </div>
              );
    }

    render() {
      return (
        <div className='popup'>
            {this.renderMessage()}
            <button type = 'close' onClick={this.props.closePopedMessage}>close me</button>
            <button type = 'edit' onClick={this.props}>save change</button>
        </div>
      );
    }
}
const mapStateToProps = (state) => {
    return { 
        message: state.fetchReducers.popUpMessage
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
      closePopedMessage: () => {
        dispatch(closePopedMessage());
      }
    }
};
export default connect(mapStateToProps, mapDispatchToProps)(Popup);
