import React from 'react';
import './style.css'
import { connect } from 'react-redux';
import { closePopedMessage } from '../actions/popUpActions';


class Popup extends React.ComponentÂ {
    constructor(props) {
        super(props);
        this.state = {
          fromEditMode: false,
          toEditMode: false,
          catEditMode: false,
          contentEditMode: false,
          dateEditMode: false,
        };
    }

    changeEditMode = (field) => {
      switch(field){
        case 'fromEditMode':
          this.setState({
            'fromEditMode' : true
          })
          break;
        case 'toEditMode':
            this.setState({
              'toEditMode' : true
            })
          break;
          case 'catEditMode':
              this.setState({
                'toEditMode' : true
              })
            break;

            case 'toEditMode':
            this.setState({
              'toEditMode' : true
            })
          break;
          case 'toEditMode':
            this.setState({
              'toEditMode' : true
            })
          break;
        default:
          break;
      }
    }

    renderMessage = () => {
      return (
                <div className='popup_inner' >
                  {this.state.fromEditMode} ? <input type = 'text' defaultValue = {this.props.message.fromname}/> :
                      <div onDoubleClick = {this.changeEditMode('fromEditMode')}>from : {this.props.message.fromname}</div>

                  {this.state.toEditMode} ? <input type = 'text' defaultValue = {this.props.message.toname}/> :
                      <div onDoubleClick = {this.changeEditMode('toEditMode')}>to : {this.props.message.toname}</div>

                  {this.state.catEditMode} ? <input type = 'text' defaultValue = {this.props.message.category}/> :
                      <div onDoubleClick = {this.changeEditMode('catEditMode')}>category: {this.props.message.category}</div>

                  {this.state.contentEditMode} ? <input type = 'text' defaultValue = {this.props.message.textcontent}/> :
                      <div onDoubleClick = {this.changeEditMode('contentEditMode')}>content: {this.props.message.textcontent}</div>

                  {this.state.dateEditMode} ? <input type = 'text' defaultValue = {this.props.message.date.toString()}/> :
                      <div onDoubleClick = {this.changeEditMode('dateEditMode')}>date: {this.props.message.date.toString()}</div>
                </div>
              );
    }

    render() {
      return (
        <div className='popup'>
            {this.renderMessage()}
            <button type = 'close' onClick={this.props.closePopedMessage}>Cancel/Close</button>
            <button type = 'edit' onClick={this.props.uploadChange}>save change</button>
        </div>
      );
    }
}
const mapStateToProps = (state) => {
    return { 
        message: state.fetchReducers.popUpMessage
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
      closePopedMessage: () => {
        dispatch(closePopedMessage());
      }
    }
};
export default connect(mapStateToProps, mapDispatchToProps)(Popup);
