import React from 'react';
import './style.css'
import { connect } from 'react-redux';
import { closePopedMessage } from '../actions/popUpActions';


class Popup extends React.ComponentÂ {

    constructor(props) {
        super(props);
        this.uploadChange = this.uploadChange.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }
    uploadChange () {
        const data = this.state;
        this.props.postMessage('http://localhost:3001/changeMessage/' + this.props.index, data);
    }
    handleChange (event) {
        this.setState({ [event.target.name]: event.target.value });
    }

    renderMessage = () => {
      return (
                <div className='popup_inner' >
                  <form onSubmit={this.handleSubmit} onChange={this.handleChange} className="form" ref = "inputform">
                    <label htmlFor="fname">From</label>
                    <input type = 'text' name = 'fromname' defaultValue = {this.props.message.fromname}/>
                    <label htmlFor="tname">To</label>
                    <input type = 'text' name = 'toname' defaultValue = {this.props.message.toname}/>
                    <label htmlFor="Category">Category</label>
                    <input type = 'text' name = 'category' defaultValue = {this.props.message.category}/>
                    <label htmlFor="subject">Content</label>
                    <input type = 'text' name = 'textcontent' defaultValue = {this.props.message.textcontent}/>
                    <button type = 'close' onClick={this.props.closePopedMessage}>Cancel/Close</button>
                    <textarea id="subject" name="textcontent" placeholder= {this.props.message.textcontent}></textarea>
                    <center>
                        <button type="submit" value="Submit" id = "submitBtn">Submit</button>
                        <button type = 'edit' onClick={this.props.uploadChange}>save change</button>
                    </center>
                </form>

                </div>
              );
    }

    render() {
      return (
        <div className='popup'>
            {this.renderMessage()}
        </div>
      );
    }
}
const mapStateToProps = (state) => {
    return { 
        message: state.fetchReducers.popUpMessage
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
      closePopedMessage: () => {
        dispatch(closePopedMessage());
      }
    }
};
export default connect(mapStateToProps, mapDispatchToProps)(Popup);
