import React from 'react';
import './style.css'
import { connect } from 'react-redux';
import { closePopedMessage } from '../actions/popUpActions';


class Popup extends React.ComponentÂ {

    render() {
      return (
        <div className='popup'>
          <div className='popup_inner'>
          <form onSubmit={this.handleSubmit} onChange={this.handleChange} className="form" ref = "inputform">
                    <label htmlFor="fname">From</label>
                    <input type="text" id="fname" name="fromname" placeholder="Your name.."></input>
                    <label htmlFor="lname">To</label>
                    <input type="text" id="tname" name="toname" placeholder="Receiver's name.."></input>

                    <label htmlFor="Category">Category</label>
                    <select id="category" name="category">
                        <option value="A">A</option>
                        <option value="B">B</option>
                        <option value="C">C</option>
                    </select>

                    <label htmlFor="subject">Content</label>
                    <textarea id="subject" name="textcontent" placeholder="Write something.."></textarea>
                    <center>
                        <button type="submit" value="Submit" id = "submitBtn">Submit</button>
                        <button type="reset" value="Reset">Reset</button>
                    </center>
                </form>


                <div>from : {this.props.message.fromname}</div>
                <div>to : {this.props.message.toname}</div>
                <div>category: {this.props.message.category}</div>
                <div>content: {this.props.message.textcontent}</div>
                <div>date: {this.props.message.date.toString()}</div>
                <button type = 'close' onClick={this.props.closePopedMessage}>close me</button>
                <button type = 'edit' onClick={this.props}>save change</button>

          </div>
        </div>
      );
    }
}
const mapStateToProps = (state) => {
    return { 
        message: state.fetchReducers.popUpMessage
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
      closePopedMessage: () => {
        dispatch(closePopedMessage());
      }
    }
};
export default connect(mapStateToProps, mapDispatchToProps)(Popup);
