import defaultState from './defaultState';
import * as popUpActions from '../actions/popUpActions';

const popMessageReducer = (state = defaultState, action) => {
	switch(action.type){
		case popUpActions.UNVIEW_ONE:
			return Object.assign({}, state, 
				{ 
					popUp : false,
					popUpMessage : {},
				}
			);
		case popUpActions.UNVIEW_ONE:
				return Object.assign({}, state, 
					{ 
						popUp : false,
						popUpMessage : {},
					}
				);
		default:
			return state;
	}
};
export default popMessageReducer;
