import React from 'react';
import { connect } from 'react-redux';
import { clearAllMessages}  from '../actions/index.js';
import './style.css'
class ClearAll extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick(){
        this.props.clearAllMessages();
    }
	render() {
        return (
            <center>
                <button type="del" onClick = {this.handleClick} id="ClearAll">ClearAll</button>
            </center>
        );
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
      cAll: (message) => {
        dispatch(clearAllMessages(message));
      }
    }
  };
export default connect(null, { clearAllMessages })(ClearAll);;
