import React from 'react';
import './style.css'
import ClearOne from './ClearOne';
import SeeOne from './ViewOneMessage';
import { connect } from 'react-redux';
import { fetchData }  from '../actions/fetchActions';

class MessageBox extends React.Component { 
    componentDidMount(){
        this.props.fetchInitial('http://localhost:3001/getMessages');
        console.log()
        console.log(this.props.messageArray);
    }

	render() {
        return (
            <div className="messages">
                <table id = "messagetable" style={{"wordWrap":"break-word","wordBreak":"break-all"}}>
                    <tbody>
                        <tr id = "messagetableHeader">
                            <th style={{width: '50px'}}>From</th>
                            <th style={{width: '50px'}}>To</th>
                            <th style={{width: '50px'}}>Category</th>
                            <th>Content</th>
                            <th style={{width: '190px'}} >Added Date</th>
                            <th style={{width: '20px'}}>Operation</th>
                        </tr>
                        {this.props.messageArray.map( (message, idx) => {
                            console.log(this.props.messageArray);
                            return (
                                <tr id = {'messageIndex' + idx} key = {'messageIndex' + idx}>
                                    <td>{message.fromname}</td>
                                    <td>{message.toname}</td>
                                    <td>{message.category}</td>
                                    <td>{message.textcontent}</td>
                                    <td>{message.date.toString()}</td>
                                    <td><ClearOne index = {idx}/><SeeOne index = {idx}/></td>
                                </tr>
                            )
                            })
                        }
                    </tbody>
                </table>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    return { 
        messageArray: state.messageReducer.messageArray,
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
      fetchInitial: (url) => {
        dispatch(fetchData(url));
      }
    }
};

export default connect(mapStateToProps,mapDispatchToProps)(MessageBox);
