export const POST_HAS_ERRORED = 'POST_HAS_ERRORED'
export const FORM_IS_POSTING = 'FORM_IS_POSTING'
export const POST_DATA_SUCCESS = 'POST_DATA_SUCCESS'

export const hasErrored = (bool) => {
    return {
        type: POST_HAS_ERRORED,
        hasErrored: bool
    };
}

export const isPosting = (bool) => {
    return {
        type: FORM_IS_POSTING,
        isPosting: bool
    };
}

export const uploadData = (url,data) => {
    return (dispatch) => {
        dispatch(isPosting(true));
        fetch(url, {
            method: 'POST',
            mode: 'cors',
            body: JSON.stringify(data),
            headers: {'Content-Type': 'application/json'}
          })
            .then((response) => {
                if (!response.ok) {
                    throw Error(response.statusText);
                }
                dispatch(isPosting(false));
                return response;
            })
            .then((response) => {                
                console.log(response.json());
                return response.json();
            })
            .then(success => {
                dispatch(postDataSuccess(success));
            })
            .catch(() => dispatch(hasErrored(true)));
    };
}

export const postDataSuccess = (response) => {
    return {
        type: POST_DATA_SUCCESS,
        response
    };
}
