export const GEN_MESSAGE = 'GEN_MESSAGE';
export const CLEAR_ALL = 'CLEAR_ALL';
export const CLEAR_ONE = 'CLEAR_ONE';


export const generateMessage = () => {
	return {
        	type: GEN_MESSAGE,
	};
};
export const clearAllMessages = () => {
	return {
        	type: CLEAR_ALL,
	};
};
export const clearMessage = (index) => {
	return {
		type: CLEAR_ONE,
		toDelIndex : index
	};
};
